package chapter08;import java.util.concurrent.CountDownLatch;/** *  * @author tengfei.fangtf * @version $Id: CountDownLatchTest.java, v 0.1 2015-8-1 ÉÏÎç12:08:52 tengfei.fangtf Exp $ */public class CountDownLatchTest {    static CountDownLatch c = new CountDownLatch(5);    public static void main(String[] args) throws InterruptedException {        new Thread(new Runnable() {            @Override            public void run() {                System.out.println(1);                c.countDown();                System.out.println(2);            }        }).start();        c.await();        new Thread(new Runnable() {            @Override            public void run() {                c.countDown();                System.out.println(3);                c.countDown();                System.out.println(2);                c.countDown();            }        }).start();        System.out.println("3");    }}